function init() {
    "use strict";

    var $wrapper = $('#wrapper').masonry({
                        // options
                        itemSelector : '.brick',
                        isFitWidth: false,
                        isResizable: true,
                        columnWidth: 30,
                        gutterWidth: 10
                    });

    $wrapper.imagesLoaded(function () {
        $wrapper.masonry();
    });
}

$(document).ready(function () {
    "use strict";

    init();
    // Dropdown behavior for Bibliocommons top nav
 //  Adapted from "jQuery Simple Drop-Down Menu Plugin"
 //  (http://javascript-array.com/scripts/jquery_simple_drop_down_menu/)
     var dd_outer = 0;
     var dd_inner = 0;

     function topnav_close() {
       if (dd_outer && dd_inner) {
         dd_inner.removeClass('openDropDown');
         dd_outer.removeClass('openDropDown');
       }
     }

     function topnav_open() {
       topnav_close();
       dd_outer = $(this).addClass('openDropDown');
       dd_inner = $(this).find('div.dropDownContent').addClass('openDropDown');
     }

     $('#primary-links > ul > li').hoverIntent({
       over: topnav_open,
       timeout: 100,
       out: topnav_close
     });

    $('.dropDown').hover(function() {
        $(this).addClass('openDropDown');
    }, function() {
        $(this).removeClass('openDropDown');
    });

    $('.nav-open-button').click(function () {
	    $(this).toggleClass('open');
	    $('.search-open-button').removeClass('open');
		$('#search-block-form-input').removeClass('open-search');
		$('.search-options-resp').removeClass('open');
        $('#header3 .nav-wrapper').toggleClass('open-navigation');
        return false;
    });

    $('.search-open-button').click(function() {
	    $(this).toggleClass('open');
	    $('.nav-open-button').removeClass('open');
        $('#header3 .nav-wrapper').removeClass('open-navigation');
        $('#search-block-form-input').toggleClass('open-search');
        $('.search-options-resp').toggleClass('open');
        return false;
    });

    $('.search-options-resp div').click(function() {
        if ($(this).attr('class') === "catalog-search") {
            $('.search-zone-catalog').attr('checked', 'checked');
        } else {
            $('.search-zone-site').attr('checked', 'checked');
        }

        $('input[name=commit]').click();
    });

    $('#bookrecs #bookrecs-recent').click(function () {
        if (!$(this).hasClass('active')) {
            $(this).toggleClass('active').siblings().toggleClass('active');
            $('#bookrecs .booklist.recommended').hide();
            $('#bookrecs .booklist.recent').show();
        }
    });

    $('#bookrecs #bookrecs-recommended').click(function () {
        if (!$(this).hasClass('active')) {
            $(this).toggleClass('active').siblings().toggleClass('active');
            $('#bookrecs .booklist.recent').hide();
            $('#bookrecs .booklist.recommended').show();
        }
    });

    // Test for mobile clickable area:
    if ($('.brick img').css('display') === "none") {
        $('#wrapper li:not(#enews):not(#bookrecs)').css('cursor', 'pointer')
        .hover(function(){
            $(this).find('h1 a').css({
                "color" : "#0090c4",
                "text-decoration": "underline"
            });
        }
        , function () {
            $(this).find('h1 a').css({
                "color" : "inherit",
                "text-decoration": "none"
            });
        })
        .click(function(e) {
            e.preventDefault();
            window.location.href = $(this).find('h1 a').attr('href');
        });
    }
}); 
